mapper.select:
select id, productid, state, TO_CHAR(TIME + 9/24, 'yyyy-mm-dd hh24:mi:ss') AS TIME, postaddressid
from purchaselist_tb
where productid = #{productid}

mapper.update:
update purchaselist_tb
set state = #{status}
where id = #{orderNum}

mapper.select:
SELECT ALLMONEY as afterSettleSum
FROM SELLER_TB
WHERE USER_ID = #{id}
